using Cognex.Vision;
private Boolean RunTool()
{
  mTool.Run(); // a previously created and configured tool
  IRunStatus aRunStatus = mTool.RunStatus;
  if (aRunStatus.Result == ToolResultConstants.Error)
  {
    if (aRunStatus.Message != null)
    {
      MessageBox.Show(aRunStatus.Message);
    }
    return false;
  }
  else
  {
    return true;
  }
}

